// code

let select_deductibila_el = document.getElementById("id_deductibila");
let amortizare_el = document.getElementById("amortizare");
let lista_cod_clasif_el = document.getElementById(
  "lista-coduri-de-clasificare"
);
let display_coduri_clasif_el = document.getElementById(
  "display-coduri-clasificare"
);
let nume_cheltuiala_el = document.getElementById("id_nume_cheltuiala");
let cod_cheltuiala_el = document.getElementById("id_cod_de_clasificare");

let lista_initiala_cod_clasif = CODURI_CLASIFICARE.map(
  (i) => `<li>${i}</li>`
).join("");
lista_cod_clasif_el.innerHTML = lista_initiala_cod_clasif;

function highlightMatch(inputString, matchString) {
  const regex = new RegExp(`(${matchString})`, "gi");
  return inputString.replace(
    regex,
    '<span class="pico-color-amber-250">$1</span>'
  );
}

function filtreazaCodClasificare(event) {
  let val = event.target.value;

  if (val.length >= 3) {
    let coduri_filtrate = CODURI_CLASIFICARE.filter((i) => {
      return i.toLowerCase().includes(val);
    });

    lista_cod_clasif_el.innerHTML = coduri_filtrate
      .map((i) => {
        return `<li>${highlightMatch(i, val)}</li>`;
      })
      .join("");
  } else {
    lista_cod_clasif_el.innerHTML = lista_initiala_cod_clasif;
  }
}

function hideShowExtraFieldsForMijlocFix() {
  if (
    select_deductibila_el.value ==
    "Mijloc fix peste 2500 RON (ded. integral cu amortizare)"
  ) {
    amortizare_el.style.display = "grid";
    display_coduri_clasif_el.style.display = "block";
    cod_cheltuiala_el.addEventListener("input", filtreazaCodClasificare);
    nume_cheltuiala_el.addEventListener("input", filtreazaCodClasificare);
  } else {
    amortizare_el.style.display = "none";
    display_coduri_clasif_el.style.display = "none";
    nume_cheltuiala_el.removeEventListener("input", filtreazaCodClasificare);
    cod_cheltuiala_el.addEventListener("input", filtreazaCodClasificare);
  }
}

if (cod_cheltuiala_el.value.length > 0) {
  filtreazaCodClasificare({ target: { value: cod_cheltuiala_el.value } });
}

hideShowExtraFieldsForMijlocFix();

select_deductibila_el.addEventListener(
  "change",
  hideShowExtraFieldsForMijlocFix
);

cod_cheltuiala_el.addEventListener("change", (event) => {
  if (event.target.value.length > 0) {
    nume_cheltuiala_el.removeEventListener("input", filtreazaCodClasificare);
  } else {
    nume_cheltuiala_el.addEventListener("input", filtreazaCodClasificare);
  }
});
